            <tr>
                <td>{{ category.id }}</td>
                <td>{{ category.title }}</td>
                <td>
                       <a class=\"btn btn-success\" href=\"{{ path('app_category_show', {'id': category.id}) }}\"><i class=\"fa fa-eye\"></i></a>
